mapper.select:
select

from intel_system


        AND sys_name = #{sysName,jdbcType=VARCHAR}


        AND sys_url = #{sysUrl,jdbcType=VARCHAR}


        AND sys_memo = #{sysMemo,jdbcType=VARCHAR}


        AND c_date = #{cDate,jdbcType=TIMESTAMP}


        AND c_accout = #{cAccout,jdbcType=VARCHAR}


        AND u_date = #{uDate,jdbcType=TIMESTAMP}


        AND u_account = #{uAccount,jdbcType=VARCHAR}


        AND status = #{status,jdbcType=BIGINT}


        AND comp_name = #{compName,jdbcType=VARCHAR}


        AND comp_legal_person = #{compLegalPerson,jdbcType=VARCHAR}


        AND comp_pic = #{compPic,jdbcType=VARCHAR}


        AND comp_pasw = #{compPasw,jdbcType=VARCHAR}


        AND comp_email = #{compEmail,jdbcType=VARCHAR}


        AND sys_pic = #{sysPic,jdbcType=VARCHAR}


        AND sys_title = #{sysTitle,jdbcType=VARCHAR}


        AND appid = #{appid,jdbcType=VARCHAR}


        AND app_sec = #{appSec,jdbcType=VARCHAR}


        AND app_token = #{appToken,jdbcType=VARCHAR}
